import styled from "styled-components";
import { motion } from "framer-motion";
import { AiOutlineArrowRight } from "react-icons/ai";

export const Main = styled.div`
margin:0;
padding:0;
box-size:border-box;
background:white;
height:700px;
width:100%;
top:200px;
position:relative;
font-family: 'Arvo', serif;

  }
`;
export const MainGrid = styled.div`
display:grid;
grid-template-columns: 1fr 1fr;
width:100%;
column-gap:10px;
padding:100px 260px 0 260px;
  }
`;
export const Subgrid1 = styled.div`
  display:grid;
  grid-template-rows:0.2fr 0.2fr 0.3fr 0.7fr 1fr;

  }
`;

export const About = styled.div`
  color:blue;
  text-transform: uppercase;
  font-size:13px;
  font-weight:600;
`;
export const Headings = styled.div`
font-size:38px;
font-weight:700;
color:#232323;
letter-spacing: -2.5px;
width:50%;
line-height:100%;
opacity:0.8;

  
`;
export const Shortdescription = styled.div`
font-size:20px;
color:#232323;
line-height:150%;
padding:25px 0 0 0;
width:70%;
opacity:0.6;
  
`;  
export const Longdescription = styled.div`
font-size:17px;
color:#232323;
line-height:150%;
padding:25px 0 35px 0;
width:70%;
opacity:0.8;

  
`;
export const Button = styled.button`
height:50px;
width:180px;
font-size:15px;
font-weight:700;
background:linear-gradient(104deg, rgba(11,15,214,1) 0%, rgba(43,117,195,1) 54%, rgba(113,153,222,1) 98%);
cursor:pointer;
color:white;
border:none;
border-radius:7px;
  &:hover{
    background:linear-gradient(104deg, rgba(113,153,222,1) 0%, rgba(43,117,195,1) 54%,  rgba(11,15,214,1) 98%);
  }

`;

export const Subgrid2 = styled.div`
display:grid;
grid-template-rows:0.1fr 0.1fr;
grid-template-columns:0.4fr 0.4fr;
padding:10px;
column-gap:80px;
row-gap:10px;
  }
`;
export const SuperSubgrid1 = styled.div`
display:grid;
grid-template-rows:0.1fr 0.1fr 0.1fr;
row-gap:15px;
  }
`;
export const Logo = styled.div`
align-self:center;
justify-self:center;

  }
`;
export const Head = styled.div`
font-size:17px;
font-weight:800;
justify-self:center;
opacity:0.5;
  }
`;
export const Description = styled.div`
justify-self:center;
align-self:center;

  }
`;
