import styled from 'styled-components';

export const Landingsection = styled.div`
 marign:0;
 padding:0;
 height:710px;
 width:100%;
 background:#162082;
 position:relative;
`;

export const Image = styled.img`
position:absolute;
height:800px;
width:700px;
top:-50px;
left:0;
`;




export const Landingheader = styled.div`
display:grid;
background:#162082;
left:860px;
row-template-columns:repeat(1fr,3);
position:absolute;
top:200px;
row-gap:25px;
`;
export const Landingpageheading = styled.div`
color:white;
font-size:2.8em;
font-weight:bold;
width:55%;
text-shadow: 12px 12px #16208;
`;
export const Landingdescription = styled.div`

color:white;
font-size:1.3em;
width:65%;

`;
export const Landingbutton = styled.div`



`;
export const Button = styled.button`

height:55px;
width:140px;
background:white;
border-radius:5px;
color:black;
border:none;
font-weight:bold;
&:hover{
    background: rgba(255, 255, 255, 0); 
    color:white;
    border:1px solid white;
}

`;
