import styled from "styled-components";
import { motion } from "framer-motion";



export const Main = styled.div`
margin:0;
padding:80px 170px 0 170px;
box-size:border-box;
background:white;
height:900px;
width:100%;
position:relative;
font-family: 'Arvo', serif;
@media screen and (max-width: 768px) {
  top:100px;
  display:none;
    }
  }
`;
export const MainGrid = styled.div`
display:grid;
grid-template-rows: 0.2fr 1fr;
row-gap:60px;
@media screen and (max-width: 768px) {

}
`;
export const Subgrid1 = styled.div`
display:grid;
grid-template-columns: 0.7fr 1fr;


}
`;

export const SupersubGrid1 = styled.div`
display:grid;
grid-template-rows: 0.1fr 1fr;
@media screen and (max-width: 768px) {

}
`;
export const Subgrid2 = styled.div`
display:grid;
grid-template-columns: 1fr 1fr 1fr;
grid-template-rows: 1fr 1fr;
column-gap:30px;
row-gap:30px;
@media screen and (max-width: 768px) {

}
`;
export const SuperSubgrid2 = styled.div`
display:grid;
grid-template-rows: 0.5fr 0.5fr 1fr;
box-shadow: 0 8px 16px 0 rgba(0, 0, 0, 0.09), 0 10px 24px 0 rgba(0, 0, 0, 0.09);
row-gap:15px;
border-radius:5px;
cursor:pointer;
padding:40px;
width:370px;
height:290px;
@media screen and (max-width: 768px) {

}
`;



export const Heading = styled.div`
color:blue;
text-transform: uppercase;
font-size:13px;
font-weight:600;
`

export const Subheading = styled.div`
font-size:40px;
font-weight:800;

opacity:0.7;`

export const Description = styled.div`
cursor:pointer;
font-size:25px;
max-width:80%;
padding:10px;
`

export const Head = styled(motion.div)`
color:blue;
text-transform: uppercase;
font-size:15px;
font-weight:600;
`
export const Desc = tyled(motion.div)`
cursor:pointer;
font-size:17px;
opacity:0.7;

`

export const Button = styled.button`
height:50px;
width:180px;
font-size:15px;
font-weight:700;
background:linear-gradient(104deg, rgba(11,15,214,1) 0%, rgba(43,117,195,1) 54%, rgba(113,153,222,1) 98%);
cursor:pointer;
color:white;
border:none;
border-radius:7px;
  &:hover{
    background:linear-gradient(104deg, rgba(113,153,222,1) 0%, rgba(43,117,195,1) 54%,  rgba(11,15,214,1) 98%);
  }
 
`;
